$('.side_panel_toggle').click(function(){
	$('body').toggleClass("small");
	$('#side-menu').toggleClass('mblmenu');
	$('.sidebar').toggleClass('sidebar_menu');
	$('#page-wrapper').toggleClass('margin20');
	$('.header').toggleClass('margin20');
	$('.bread-crumb').toggleClass('margin20');
	$('.footer').toggleClass('margin20');
	$('#side-menu').toggleClass('mobilemenu');

});
$('.mobile-header').click(function(){
	$('.header').slideToggle('open');
});





